package orchestrator

import (
	"context"
	"encoding/json"
	"log"
	"time"

	"github.com/anthropics/anthropic-sdk-go"
	"github.com/anthropics/anthropic-sdk-go/option"
	"github.com/hibiken/asynq"
	"github.com/jackc/pgx/v5/pgxpool"
)

// Task Types
const TypeAgentRun = "agent:run"

type AgentRunPayload struct {
	JobID    string `json:"job_id"`
	ConfigID string `json:"config_id"`
	Prompt   string `json:"prompt"`
}

type AgentProcessor struct {
	DB      *pgxpool.Pool
	Claude  *anthropic.Client
}

func NewAgentProcessor(db *pgxpool.Pool, apiKey string) *AgentProcessor {
	return &AgentProcessor{
		DB:     db,
		Claude: anthropic.NewClient(option.WithAPIKey(apiKey)),
	}
}

func (p *AgentProcessor) ProcessTask(ctx context.Context, t *asynq.Task) error {
	var payload AgentRunPayload
	if err := json.Unmarshal(t.Payload(), &payload); err != nil {
		return err
	}

	// 1. Fetch Configuration & Update Job Status
	// (Simulated DB call)
	log.Printf("Processing Job %s for Config %s", payload.JobID, payload.ConfigID)

    // 2. Execute Claude Request
	resp, err := p.Claude.Messages.New(ctx, anthropic.MessageNewParams{
		Model:     anthropic.F(anthropic.ModelClaude3_5Sonnet20240620),
		MaxTokens: anthropic.F(int64(1024)),
		Messages: anthropic.F([]anthropic.MessageParam{
			anthropic.NewUserMessage(anthropic.NewTextBlock(payload.Prompt)),
		}),
	})
	if err != nil {
		// Log error to DB here
		return err
	}

	responseText := resp.Content[0].Text
	log.Printf("Claude Response: %s", responseText)

