#### **B. Configuration Management View (React)**

**File:** `ConfigEditor.tsx`
*Uses JSON Schema to auto-generate the configuration form.*

```tsx
import React, { useState } from 'react';
import Form from '@rjsf/core';
import validator from '@rjsf/validator-ajv8';

// JSON Schema definition for Claude Configuration
const schema = {
  title: "Claude Agent Configuration",
  type: "object",
  required: ["name", "model", "system_prompt"],
  properties: {
    name: { type: "string", title: "Agent Name", default: "My Research Assistant" },
    model: { 
      type: "string", 
      title: "Model Version", 
      enum: ["claude-3-5-sonnet-20240620", "claude-3-opus-20240229"],
      default: "claude-3-5-sonnet-20240620"
    },
    system_prompt: { 
      type: "string", 
      title: "System Prompt", 
      format: "textarea" 
    },
    parameters: {
      type: "object",
      title: "Hyperparameters",
      properties: {
        temperature: { type: "number", title: "Temperature", minimum: 0, maximum: 1, step: 0.1, default: 0.7 },
        max_tokens: { type: "integer", title: "Max Tokens", default: 1024 }
      }
    }
  }
};

const uiSchema = {
  system_prompt: {
    "ui:widget": "textarea",
    "ui:options": { rows: 8 },
    "ui:classNames": "font-mono text-sm" // Tailwind utility classes
  }
};

export const ConfigEditor = () => {
  const [formData, setFormData] = useState(null);

  const handleSubmit = async ({ formData }: any) => {
    // API Call to Backend
    console.log("Saving Config:", formData);
    await fetch('/api/configs', {
        method: 'POST',
        body: JSON.stringify(formData)
